    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        //int idQuestao = Integer.parseInt(idquestao.getText());
        String tit = titulo.getText();
        String A = alterA.getText();
        String B = alterB.getText();
        String C = alterC.getText();
        String D = alterD.getText();
        String E = alterE.getText();
        String respostaChar = resp.getText();
        int resposta = 0;
        
        switch(respostaChar){
            case "A": 
                resposta = 1;
            break;
            case "B":
                resposta = 2;
            break;
            case "C":
                resposta = 3;
            break;
            case "D":
                resposta = 4;
            break;
            case "E":
                resposta = 5;
            break;
            //default: 
        }
        //int resposta = Integer.parseInt(resp.getText());
        
        
       
        Questao q = new Questao(tit, A, B, C, D, E, resposta);
        sql.updateQuestoes(q.getTitulo(),q.getAlternativaA(),q.getAlternativaB(), q.getAlternativaC(),q.getAlternativaD(), q.getAlternativaE(), q.getResposta());
    }//GEN-LAST:event_jButton1ActionPerformed

